import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [searchText, setSearchText] = useState("");
  const [gameList, setGameList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  function getPlayerGames() {
    setLoading(true);
    axios.get("http://localhost:4000/past5Games", { params: { username: searchText}})
    .then(function (response) {
      setGameList(response.data);
      setLoading(false);
      setError(null);
    }).catch(function (error) {
      setError("An error occurred. Please try again later.");
      setLoading(false);
      console.log(error);
    });
  }

  return (
    <div className="App">
      <h2>Welcome to my proxy server app!</h2>
      <input type="text" onChange={e => setSearchText(e.target.value)}></input>
      <button onClick={getPlayerGames}>Get the past 5 games from your player</button>
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      <div className="game-container">
        {gameList.map((gameData, index) => (
          <div className="game-box" key={index}>
            <h2>Game {index + 1} - Game Mode: {gameData.info.gameMode}</h2>
            <div>
              {gameData.info.participants.map((data, participantIndex) =>
                <div key={participantIndex}>
                  <img src={`https://ddragon.leagueoflegends.com/cdn/14.6.1/img/champion/${data.championName}.png`} alt={data.championName} />
                  <p>Player {participantIndex + 1} : {data.summonerName}, Champion : {data.championName}, KDA {data.kills} / {data.deaths} / {data.assists}</p>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
